let form=document.querySelector(".authorization__form"),input=form.querySelector(".authorization__email"),submit=form.querySelector(".authorization__btn");form.addEventListener("submit",(t=>{input.value||(t.preventDefault(),input.placeholder="Введите e-mail",input.classList.add("authorization__email--invalid"))})),input.addEventListener("focus",(()=>{input.placeholder="Введите текст",input.classList.remove("authorization__email--invalid")})),input.addEventListener("blur",(()=>{input.placeholder="E-mail"}));